import React from 'react';
import './index.css';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';

import { Button, Modal, Form, Input, Radio } from 'antd';

const CollectionCreateForm = Form.create({ name: 'form_in_modal' })(
  // eslint-disable-next-line
  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="Cadastro de cliente"
          okText="Cadastrar"
          cancelText="Cancelar"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical" labelCol={{ span: 5 }} wrapperCol={{ span: 12 }}>
            <Form.Item label="Nome">
              {getFieldDecorator('nome', {
                rules: [{ required: true, message: 'Por favor, insira os dados!' }],
              })(<Input />)}
            </Form.Item>
            <Form.Item label="Telefone">
              {getFieldDecorator('telefone', {
                rules: [{ required: true, message: 'Por favor, insira os dados!' }],
              })(<Input />)}
            </Form.Item>
            <Form.Item label="E-mail">
              {getFieldDecorator('e-mail', {
                rules: [{ required: true, message: 'Por favor, insira os dados!' }],
              })(<Input />)}
            </Form.Item>
          </Form>
        </Modal>
      );
    }
  },
);

class CollectionsPage extends React.Component {
  state = {
    visible: false,
  };

  showModal = () => {
    this.setState({ visible: true });
  };

  handleCancel = () => {
    this.setState({ visible: false });
  };

  handleCreate = () => {
    const { form } = this.formRef.props;
    form.validateFields((err, values) => {
      if (err) {
        return;
      }

      console.log('Received values of form: ', values);
      form.resetFields();
      this.setState({ visible: false });
    });
  };

  saveFormRef = formRef => {
    this.formRef = formRef;
  };

  render() {
    return (
      <div>
        <h1 className="titulo" >Capacitação 2020 - Férias</h1>
        <Button type="primary" className="mod" onClick={this.showModal}>
            Cadastrar
        </Button>
        <CollectionCreateForm
          wrappedComponentRef={this.saveFormRef}
          visible={this.state.visible}
          onCancel={this.handleCancel}
          onCreate={this.handleCreate}
        />
      </div>
    );
  }
}
export default CollectionsPage;
