import React from 'react';
import './index.css';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';

import { Button, Modal, Form, Input, List, Avatar } from 'antd';

const data = [];
const dados = [
  {
    title: 'Ant Design Title 1',
  },
  {
    title: 'Ant Design Title 2',
  },
  {
    title: 'Ant Design Title 3',
  },
  {
    title: 'Ant Design Title 4',
  },
];

const CollectionCreateForm = Form.create({ name: 'form_in_modal' })(

  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="Cadastrar de cliente"
          okText="Cadastrar"
          cancelText="Cancelar"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical" labelCol={{ span: 5 }} wrapperCol={{ span: 12 }}>
            <Form.Item label="Nome">
              {getFieldDecorator('nome', {
                rules: [{ required: true, message: 'Por favor, insira os dados!' }],
              })(<Input />)}
            </Form.Item>
            <Form.Item label="Telefone">
              {getFieldDecorator('telefone', {
                rules: [{ required: true, message: 'Por favor, insira os dados!' }],
              })(<Input />)}
            </Form.Item>
            <Form.Item label="E-mail">
              {getFieldDecorator('email', {
                rules: [{ required: true, message: 'Por favor, insira os dados!' }],
              })(<Input />)}
            </Form.Item>
          </Form>
        </Modal>
      );
    }
  },
);

class CollectionsPage extends React.Component {
  state = {
    visible: false,
  };

  showModal = () => {
    this.setState({ visible: true });
  };

  handleCancel = () => {
    this.setState({ visible: false });
  };

  handleCreate = () => {
    const { form } = this.formRef.props;
    form.validateFields((err, values) => {
      if (err) {
        return;
      }

      console.log('Received values of form: ', values.telefone);
      data.push({
        id: 1,
        nome: values.telefone,
        telefone: values.telefone,
        Email: values.email,
      });
      form.resetFields();
      this.setState({ visible: false });
    });
  };

  saveFormRef = formRef => {
    this.formRef = formRef;
  };

  render() {
    return (
      <div>
        <h1 className="subTitulo" >Agenda de contatos</h1>
        <Button type="primary" className="mod" onClick={this.showModal}>
            Cadastrar
        </Button>
        <CollectionCreateForm
          wrappedComponentRef={this.saveFormRef}
          visible={this.state.visible}
          onCancel={this.handleCancel}
          onCreate={this.handleCreate}
        />
        <br />
        <br />

        <List
          itemLayout="horizontal"
          dataSource={dados}
          renderItem={item => (
          <List.Item>
        <List.Item.Meta
          avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
          title={<a href="https://ant.design">{item.title}</a>}
          description="Ant Design, a design language for background applications, is refined by Ant UED Team"
        />
      </List.Item>
    )}
  />
      </div>
    );
  }
}
export default CollectionsPage;
